    function getBootstrapClass($pourcentage)
    {
        if ($pourcentage <= 59) {
            return 'text-danger';
        } elseif ($pourcentage <= 79) {
            return 'text-warning';
        } else {
            return 'text-success';
        }
    }

    function getclasses($grp)
    {
        $percent = round(($grp->score * 100) / ($grp->total));
        $bootstrapclass = getBootstrapClass($percent);
        return $bootstrapclass;
    }

    function getAverageClasses($grp1, $grp2, $scoress)
    {
        // Calculer le pourcentage pour les deux groupes
        $percent1 = ($grp1->score * 100) / ($grp1->total);
        $percent2 = ($scoress * 100) / ($grp2->total);

        // Calculer la moyenne
        $averagePercent = ceil(($percent1 + $percent2) / 2);

        // Retourner la classe Bootstrap correspondante
        return getBootstrapClass($averagePercent);
    }

