    /**
     *  ‚ñ∏ Entr√©es
     *    $quizResults  : tableau associatif [speciality] => document BSON (ou null)
     *    $quizTest     : r√©sultat d‚Äôagr√©gation contenant sumTotal (valeur > 0)
     *    $level        : "Junior" | "Senior" | "Expert"
     *  ‚ñ∏ Sorties
     *    [
     *      'scores'   => [speciality => int],  // scores individuels apr√®s √©quilibrage
     *      'total'    => int,                  // somme finale (100 ou 50 selon niveau)
     *    ]
     */

     function computeLevelScores(array $quizResults, array $quizTest, string $level): array
     {
         // --- Param√©trage par niveau ---
         $targets = [
             'Junior' => 100,
             'Senior' => 100,
             'Expert' => 50,
         ];
         if (!isset($targets[$level])) {
             throw new InvalidArgumentException("Niveau inconnu : $level");
         }
         $target     = $targets[$level];
         $multiplier = $target;                    // m√™me √©chelle que le total cible
         $sumTotal   = (int) ($quizTest[0]['sumTotal'] ?? 0);
         if ($sumTotal <= 0) {
             throw new RuntimeException('sumTotal doit √™tre strictement positif');
         }
     
         // --- Calcul brut des scores par sp√©cialit√© ---
         $scores = [];
         foreach ($quizResults as $spec => $doc) {
             if ($doc !== null && isset($doc['total'])) {
                 $scores[$spec] = (int) round(($doc['total'] * $multiplier) / $sumTotal, 0);
             }
         }
     
         // --- Somme et √©cart ---
         $currentTotal = array_sum($scores);
         $diff         = $currentTotal - $target;
     
         if ($diff !== 0) {
             // Priorit√©s d‚Äôajustement (√©quivalent aux variables number29, number20, ‚Ä¶)
             switch ($level) {
                 case 'Junior':
                     $priority = ['Transversale', 'Moteur Thermique', 'Electricit√© et Electronique', 'Moteur Diesel'];
                     break;
                 case 'Senior':
                     $priority = ['Moteur Diesel', 'Electricit√© et Electronique', 'Moteur Thermique'];
                     break;
                 case 'Expert':
                     $priority = ['Moteur Thermique', 'Transversale', 'Freinage']; // (‚âÉ number220, number290, number120)
                     break;
                 default:
                     $priority = []; // ne devrait jamais arriver gr√¢ce au test pr√©c√©dent
             }
     
             // Ajustement incr√©mental / d√©cremental en boucle circulaire
             $index = 0;
             while ($diff !== 0 && !empty($priority)) {
                 $key = $priority[$index % count($priority)];
     
                 // La sp√©cialit√© peut √™tre absente si le quiz n‚Äôexiste pas ‚Üí on passe
                 if (!isset($scores[$key])) {
                     $index++;
                     continue;
                 }
     
                 // Sens d‚Äôajustement : -1 si > target, +1 si < target
                 $step     = ($diff > 0) ? -1 : 1;
                 $proposed = $scores[$key] + $step;
     
                 // Interdiction de descendre sous 0
                 if ($proposed >= 0) {
                     $scores[$key] = $proposed;
                     $diff        += $step;   // on se rapproche de 0
                 }
                 $index++;
             }
         }
     
         // --- S√©curit√© : on force la somme exactement au target ---
         $gap = $target - array_sum($scores);           // ¬±1 max apr√®s la boucle
         if ($gap !== 0) {
             // on l‚Äôajoute (ou retranche) √† la premi√®re sp√©cialit√© disponible
             $firstKey = array_key_first($scores);
             $scores[$firstKey] += $gap;
         }
     
         ksort($scores, SORT_STRING);                   // rangement alphab√©tique (optionnel)
         return ['scores' => $scores, 'total' => array_sum($scores)];
     }
     

