        /**
         * Cr√©e ou incr√©mente un r√©sultat dans la collection ¬´‚ÄØresults‚ÄØ¬ª.
         */
        function upsertResult(ObjectId $userId, ObjectId $quizId, array $qIds, array $answers,
                            int $score, object $quizDoc, string $level, string $time,
                            MongoDB\Collection $results): void {

            $filter = [
                'user'       => $userId,
                'speciality' => $quizDoc->speciality,
                'type'       => $quizDoc->type,
                'level'      => $level,
            ];

            $previous = $results->find($filter)->toArray();

            $payload = [
                'questions'   => $qIds,
                'answers'     => $answers,
                'quiz'        => $quizId,
                'user'        => $userId,
                'score'       => $score,
                'speciality'  => $quizDoc->speciality,
                'level'       => $level,
                'type'        => $quizDoc->type,
                'total'       => count($qIds),
                'numberTest'  => count($previous) + 1,
                'time'        => $time,
                'active'      => true,
                'created'     => date('d-m-Y H:i:s'),
            ];

            $results->insertOne($payload);
        }

        /**
         * √âvalue les r√©ponses pour une sp√©cialit√© donn√©e et retourne [score, qIds, labels].
         */
        function evaluateSpeciality(array $questionsTag, array $userAnswers, string $speciality,
                                    MongoDB\Collection $questions): array {
            $score     = 0;
            $qIds      = [];
            $proposals = [];

            foreach ($questionsTag as $idx => $qIdHex) {
                /** @var object $qData */
                $qData = $questions->findOne([
                    '_id'   => oid($qIdHex),
                    'active'=> true,
                ]);

                if (!$qData || $qData->speciality !== $speciality) {
                    continue; // question d'une autre sp√©cialit√©
                }

                $isCorrect = $userAnswers[$idx] === $qData->answer;
                $score    += $isCorrect ? 1 : 0;
                $proposals[] = $isCorrect ? 'Maitris√©' : 'Non maitris√©';
                $qIds[]      = $qData->_id;
            }

            return [$score, $qIds, $proposals];
        }

